import { useState, useEffect } from 'react'
import { db } from './services/firebaseServices';
import { ref, onValue, query, limitToLast } from "firebase/database";
import { Header, Stats, Control } from './components/components'


const App = () => {
  const [currentTemperature, setCurrentTemperature] = useState()
  const [temperatures, setTemperatures] = useState([])
  const [relays, setRelays] = useState([['Relay 1', false], ['Relay 2', false], ['Relay 3', false]])






  useEffect(() => {
    onValue(ref(db, 'mlx90614/current'), snapshot => {
      const data = snapshot.val()
      setCurrentTemperature(data.ambient)
    })

    onValue(query(ref(db, "mlx90614/history/"), limitToLast(20)) , snapshot => {
      let childData = []
      snapshot.forEach((childSnapshot) => {
        childData.push(childSnapshot.val())
      })
      setTemperatures(childData)
    }, //{onlyOnce: true}
    );

    onValue(ref(db, 'Relays'), snapshot => {
      const data = snapshot.val()
      setRelays([['Relay 1', data.Relay1], ['Relay 2', data.Relay2], ['Relay 3', data.Relay3]])
    })
  }, [])
  return (
    <div className="container">
      <Header />
      <Stats currentTemperature={currentTemperature} temperatures={temperatures} />
      <Control relays={relays} />
    </div>
  )
}

export default App;
